import React from 'react'
import { useState } from 'react';
import {Link} from 'react-router-dom'
import './Style.css';
import RoomDetails from './RoomDetails';

//זו הקומפוננטה הבסיסית של המוצר, תוצג בעמוד הבית רק עם השם. 
//in RoomDetails we will see all the room details-זה אמור להיפתח בלחיצה

export default function Room(props) {
  const[bgRoomColor,setRoomColor] = useState('');
  const [flag,setFlag] = useState(false)

  const showRoomDetails=()=>{
      if(flag==true){
      return <RoomDetails roomName={props.roomName} roomType={props.roomType} roomColor={props.roomColor}roomProducts={props.roomProducts}/>
      }}

  return (
    <div onClick={()=>{setFlag(!flag)}} style={{backgroundColor:props.roomColor}} className='room'>      
        <h2>Room Name:{props.roomName}</h2>
        <h3>Room Type:{props.roomType}</h3>
        {/* <h3>Room color:{props.roomColor}</h3>
        <h3>Room products:{props.roomProducts}</h3> */}

        <Link to='/add-Product'><button>Add Product</button></Link>
        {showRoomDetails()}
    </div>
  )
}
