import {BrowserRouter,Route,Routes} from 'react-router-dom';
import { useState } from 'react';
import './App.css';
import Title from './components/Title';
import HomePage from './components/HomePage';
import AddRoom from './components/AddRoom';
import AllRooms from './components/AllRooms';
import Room from './components/Room';
import AddProduct from './components/AddProduct';
import Product from './components/Product';


function App() {
  const [rooms,setRooms] =  useState([{roomType:'saloon', roomName: 'salon1' , roomColor: 'red',roomProducts: ['boiler' , 'stereo']}]);

  const addNewRoomToArr = (newRoomType,newRoomName,newRoomColor,newRoomProducts)=>{
    let tempRoom ={
      roomType: newRoomType,
      roomName: newRoomName,
      roomColor: newRoomColor,
      roomProducts: newRoomProducts
    }
    setRooms([...rooms,tempRoom])

    console.log(rooms);
  }

  return (
    <div className="App">
      <Title/>
      <BrowserRouter> 
    {/* //האם פה לשים תפריט ניווט? לשאול את אוהד */}
        <Routes>
          <Route path = '/' element = {<HomePage arr={rooms}/>} />
          {/* <Route path='/' element={<HomePage arr={rooms.map((val,i)=>{
            return <Room index={i} roomName={val.roomName} roomType={val.roomType} roomColor={val.roomColor} roomProducts={val.roomProducts}/>
            })}/>}/> */}

          <Route path='/add-room' element={<AddRoom add={addNewRoomToArr}/>}/>

          <Route path='/rooms' element={<AllRooms arr={rooms.map((val,i)=>{
            return <Room index={i} roomName={val.roomName} roomType={val.roomType} roomColor={val.roomColor} roomProducts={val.roomProducts}/>
            })}/>}/>

          <Route path='/room' element={<Room/>}/>
          {/* <Route path='/room' element={<Room oneRoom={rooms((val,i)=>{
            return <Room index={i} roomName={val.roomName} roomType={val.roomType} roomColor={val.roomColor} roomProducts={val.roomProducts}/>
            })}/>}/> */}

          <Route path='/add-Product' element={<AddProduct/>}/>
          <Route path='/product' element={<Product productName={rooms.roomProducts}/>}/>
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;

