import React,{useState} from 'react';
import {Link} from 'react-router-dom'
import './Style.css';

export default function AddRoom(props) {
  //hooks for the rooms adding//
  const[roomType,setRoomType] = useState('');
  const[roomName,setRoomName] = useState('');
  const[roomColor,setRoomColor] = useState('');

  const validRoom=()=>{
    if(roomName.length > 5){
      alert ('ERROR');
      <Link to='/'></Link>
    }
    else{
      {<props.add></props.add>}
      <Link to='/'></Link>

    }
  }

  return (
    <div>
      <h1>Add room</h1>
            <select className='selectRoom' id='roomTypeSelect' name='room-type-select'>
              <option value="selectRoom">Please  Choose Room Type</option>
              <option value="Bedroom">Bedroom</option>
              <option value="BathroomToilet">Bathroom\Toilet</option>
              <option value="kitchen">kitchen</option>
            </select> <br/>

            <input type='text' className='inpt' id='roomNameInpt' placeholder='Please Insert Room Name'/><br/>
            <input type='color' className='inpt' id='roomColorInpt'placeholder='Please Choose Room Color'/><br/>

            <button onClick={validRoom()} id='AddRoomBtn'>Add Room</button>

    </div>
  )
}
