import React from 'react'
import { useState } from 'react';
import {Link} from 'react-router-dom'
import './Style.css';

export default function Product() {

    //hook for changing color on click - losson 19, 34:00
    const[bgColorOnOff,setColor] = useState('');

    const changeColor =()=>{
      if(bgColorOnOff=='green')
        setColor('red')
      else{
        setColor('green')
      }
    }

  return (
    <div>
        <div style={{backgroundColor:bgColorOnOff}}id='product'>
          <button onClick={changeColor()}> product name: </button>
          
          </div>
    </div>
  )
}
