import React from 'react'
import { useState } from 'react';
import {Link} from 'react-router-dom'
import './Style.css';

//פה פרטי החדר המלאים, זה מה שאמור להיפתח כשנלחץ על חדר בעמוד הבית או באולרומס

export default function RoomDetails(props) {
  let i = props.index;

  return (
    <div>
        <div className='room'>      
        <h2>Room Name:{props.roomName(i)}</h2>
        <h3>Room Type:{props.roomType[i]}</h3>
        <h3>Room color:{props.roomColor[i]}</h3>
        <h3>Room products:{props.roomProducts[i]}</h3>

        <Link to='/add-Product'><button>Add Product</button></Link>
    </div>
    </div>
  )
}
