import React,{useState} from 'react';
import {Link} from 'react-router-dom'
import './Style.css';


export default function AddProduct(props) {

  const [roomProducts,setRoomProducts] = useState('[]');

  const validProduct=()=>{
    if(roomProducts.length == 5){
    alert('ERROR')
    }
    





    else{
      {props.add(roomProducts)};
      return true    }
  }

  return (
    <div>
        <h1>Add Product to Your Room</h1>

            <select onChange={(element)=>{setRoomProducts(element.target.value)}}className='selectproduct' id='productTypeSelect' name='product-type-select'>
              <option value="select">Please Select Product Type</option>
              <option value="ac">A/C</option>
              <option value="Boiler">Boiler</option>
              <option value="Stereo System">Stereo System</option>
              <option value="Lamp">Lamp</option>
            </select>

            <button onClick={validProduct} id='AddProdBtn'>Add Product</button>
    </div>
  )
}
