import React from 'react'
import { useState } from 'react';
import {Link} from 'react-router-dom'
import './Style.css';


export default function Room(props) {

  return (
    <div className='room'>
      {props.oneRoom=((val,i)=>{
            return <Room index={i} roomName={val.roomName} roomType={val.roomType} roomColor={val.roomColor} roomProducts={val.roomProducts}/>
          })}/>}/>
        <h1>Room Name:{props.roomName}</h1>
        <h2>Room Type:{props.roomType}</h2>
        <h2>Room color:{props.roomColor}</h2>
        <h2>Room products:{props.roomProducts}</h2>

        <Link to='/add-Product'><button>Add Product</button></Link>
    </div>
  )
}
