import React from 'react'
import { useState } from 'react';
import {Link} from 'react-router-dom'
import './Style.css';


export default function Room(props) {
  // const bgRoomColor = {oneRoom.roomColor.val};

  return (
    <div className='room'>
      {props.oneRoom((val,i)=>{
        return<>
          <h1>Room Name:{val.roomName}</h1>
          <h2>Room Type:{val.roomType}</h2>
          <h2>Room color:{val.roomColor}</h2>
          <h2>Room products:{val.roomProducts}</h2>
        </>
        }
        )}
          
        {/* <h1>Room Name:{val.roomName}</h1>
        <h2>Room Type:{val.roomType}</h2>
        <h2>Room color:{val.roomColor}</h2>
        <h2>Room products:{val.roomProducts}</h2> */}

        <Link to='/add-Product'><button>Add Product</button></Link>
    </div>
  )
}
